import React, { useState, useEffect } from "react";

function Stream({ socket }) {
  const [roomName, setRoomName] = useState("");
  const [userName, setUserName] = useState("");
  const [streamState, setStreamState] = useState(false);
  const [audioState, setAudioState] = useState(false);
  const [videoState, setVideoState] = useState(false);

  // mideo 제약 정의
  const mediaConstraints = {
    audio: true,
    video: {
      height: 360,
    },
  };

  //stream 시작
  const startStream = () => {
    navigator.mediaDevices
      .getUserMedia(mediaConstraints)
      .then((stream) => {
        const myVideo = document.getElementById("local_vid");
        myVideo.srcObject = stream;
        setStreamState(true);
        setAudioMuteState(audioState);
        setVideoMuteState(videoState);
      })
      .catch((error) => {
        console.log(`startStream Error ${error}`);
      });
  };

  //audio 음소거 설정
  const setAudioMuteState = (flag) => {
    let muteIcon = document.getElementById("mute_icon");
    if (muteIcon) {
      let localStream = document.getElementById("local_vid").srcObject;
      localStream.getAudioTracks().forEach((track) => {
        track.enabled = !flag;
      });
      // switch button icon
      muteIcon.innerText = flag ? "mic_off" : "mic";
      setAudioState(flag); // 추가: setAudioState 호출
    }
  };

  //medio 음소거 설정
  const setVideoMuteState = (flag) => {
    let vidMuteIcon = document.getElementById("vid_mute_icon");
    if (vidMuteIcon) {
      let localStream = document.getElementById("local_vid").srcObject;
      localStream.getVideoTracks().forEach((track) => {
        track.enabled = !flag;
      });
      // switch button icon
      vidMuteIcon.innerText = flag ? "videocam_off" : "videocam";
      setVideoState(flag); // 추가: setVideoState 호출
    }
  };

  //stream 준비 이벤트 요청
  const readyForStream = (event) => {
    event.preventDefault();

    startStream();

    if (roomName.trim() && userName.trim()) {
      socket.emit("readyForStream", roomName, userName);
    }
  };

  useEffect(() => {
    const handleReadyForStreamSuccess = ({ roomName }) => {
      console.log("ready for stream success");
      console.log(roomName);
      socket.emit("join_room", roomName);
    };

    // const handleUserJoin = (data) => {
    //   console.log("New user join", data.sid, data.roomName);
    //   let peer_id = data["sid"];
    //   let userName = data["name"];
    //   // _peer_list[peer_id] = undefined; // _peer_list가 정의되지 않아 일단 주석 처리
    // };

    //socket 통신
    socket.on("readyForStreamSuccess", handleReadyForStreamSuccess);
    //socket.on("user_join", handleUserJoin);

    // useEffect의 cleanup 함수를 사용하여 컴포넌트가 언마운트될 때 리스너를 제거
    return () => {
      socket.off("readyForStreamSuccess", handleReadyForStreamSuccess);
      //   socket.off("user_join", handleUserJoin);
    };
  }, [socket]); // socket이 변경될 때마다 useEffect를 다시 실행

  return (
    <div>
      <form onSubmit={readyForStream}>
        <input value={roomName} onChange={(e) => setRoomName(e.target.value)} />
        <input value={userName} onChange={(e) => setUserName(e.target.value)} />
        <button type="submit">Join!</button>
      </form>
      {/* 비디오를 표시할 비디오 요소 */}
      <video id="local_vid" autoPlay muted playsInline />

      {/* 음성 음소거 버튼 */}
      <button id="vid_mute_icon" onClick={() => setAudioMuteState(!audioState)}>
        {audioState ? "Unmute Audio" : "Mute Audio"}
      </button>

      {/* 비디오 음소거 버튼 */}
      <button id="vid_mute_icon" onClick={() => setVideoMuteState(!videoState)}>
        {videoState ? "Unmute Video" : "Mute Video"}
      </button>
    </div>
  );
}

export default Stream;
