import { useState, useEffect } from "react";

function Stream({ socket }) {
  const [roomName, setRoomName] = useState("");
  const [userName, setUserName] = useState("");
  const [cameraState, setCameraState] = useState(false);
  const [audioState, setAudioState] = useState(false);

  useEffect(() => {
    const handleReadyForStreamSuccess = ({ roomName }) => {
      console.log("ready for stream success");
      console.log(roomName);
      socket.emit("join_room", roomName);
    };

    socket.on("readyForStreamSuccess", handleReadyForStreamSuccess);

    socket.on("user_join", (data) => {
      console.log("New user join", data.sid, data.roomName);
      let peer_id = data["sid"];
      let userName = data["name"];
      _peer_list[peer_id] = undefined;
    });

    // useEffect의 cleanup 함수를 사용하여 컴포넌트가 언마운트될 때 리스너를 제거
    return () => {
      socket.off("readyForStreamSuccess", handleReadyForStreamSuccess);
    };
  }, [socket]); // socket이 변경될 때마다 useEffect를 다시 실행

  const readyForStream = (event) => {
    event.preventDefault();

    startCamera();

    if (roomName.trim() && userName.trim()) {
      socket.emit("readyForStream", roomName, userName);
    }
  };

  const mediaConstraints = {
    audio: true,
    video: {
      height: 360,
    },
  };

  function startCamera() {
    navigator.mediaDevices
      .getUserMedia(mediaConstraints)
      .then((stream) => {
        myVideo.srcObject = stream;
        setCameraState(true);
        setAudioMuteState(audioMuted);
        setVideoMuteState(videoMuted);
      })
      .catch((error) => {
        console.log(`StartCamera Error ${error}`);
      });
  }

  function setAudioMuteState(flag) {
    let local_stream = myVideo.srcObject;
    console.log("setAudioMuteState: ", local_stream);
    local_stream.getAudioTracks().forEach((track) => {
      track.enabled = !flag;
    });
    // switch button icon
    document.getElementById("mute_icon").innerText = flag ? "mic_off" : "mic";
  }
  function setVideoMuteState(flag) {
    let local_stream = myVideo.srcObject;
    local_stream.getVideoTracks().forEach((track) => {
      track.enabled = !flag;
    });
    // switch button icon
    document.getElementById("vid_mute_icon").innerText = flag
      ? "videocam_off"
      : "videocam";
  }

  return (
    <div>
      <form onSubmit={readyForStream}>
        <input value={roomName} onChange={(e) => setRoomName(e.target.value)} />
        <input value={userName} onChange={(e) => setUserName(e.target.value)} />
        <button type="submit">Join!</button>
      </form>
    </div>
  );
}

export default Stream;
